import React ,{ useEffect, useState } from "react";
import "./Chat.css";
import { Avatar, IconButton } from '@material-ui/core';
import AttachFileIcon from '@material-ui/icons/AttachFile';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import SearchOutlinedIcon from '@material-ui/icons/SearchOutlined';
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';


function Chat(){
  console.log("started");
    const [input, setInput] = useState("");
    const [seed , setSeed] = useState("");

    useEffect(() => {
        setSeed(Math.floor(Math.random()*50000));
    },[]);
    console.log("started img");

   const handleSubmit = (e) =>{
     e.preventDefault();
   }
    const sendMessage = (e) => {
      console.log("button not clicked");
      e.preventDefault();

      console.log("you typed",input);

      setInput('');
    };
const inputMessage = (e) =>{
  setInput(e.target.value);
}
    return (
    <div className="chat"> 

        <div className="chat__header">
         <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`}/>
         <div className="chat__headerInfo">
         <h3>Room Name</h3>
         <p>Last Seen at ..</p>
        </div>
        <div className="chat__headerRight">
         <IconButton>
         <SearchOutlinedIcon />
         </IconButton>
         <IconButton>
         <AttachFileIcon />
         </IconButton>
         <IconButton>
         <MoreVertIcon />
         </IconButton>

         </div>

        </div>

       <div className="chat__body">
         <p className={`chat__message ${true && "chat__reciever"}`}> <span className="chat__name"> Apurva </span>  HI ALL <span className="chat__timestamp"> 12:00 am</span></p>

       </div>

        <div className="chat__footer">
         <InsertEmoticonIcon />
         <form onSubmit={handleSubmit}> 
             <input value={e.target.input} onChange={inputMessage} placeholder= "Type a message" type="text" />
             <button onClick={sendMessage} type="submit">Send a Message</button>
         </form>
         <MicIcon />

        </div>

    </div> 
    );
}

export default Chat;
