import React ,{ useEffect, useState } from "react";
import "./Chat.css";
import { Avatar, IconButton } from '@material-ui/core';
import AttachFileIcon from '@material-ui/icons/AttachFile';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import SearchOutlinedIcon from '@material-ui/icons/SearchOutlined';
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';
import {useParams} from "react-router-dom";
import db from "./firebase";
import { useStateValue } from "./StateProvider";


function Chat(){
  console.log("started");
    const [input, setInput] = useState("");
    const [seed , setSeed] = useState("");
    const {roomId}  = useParams();
    const [roomName,setRoomName] = useState("");
    const [messages,setMessages] = useState([]);

    useEffect(() => {
        setSeed(Math.floor(Math.random()*50000));
    },[roomId]);

   useEffect(() =>{
     if(roomId){
      db.collection('rooms').doc(roomId).onSnapshot(snapshot => (
        setRoomName(snapshot.data().name)
      ));

      db.collection("rooms").doc(roomId)
      .collection("messages").orderBy("timestamp","asc")
      .onSnapshot((snapshot) => 
      setMessages(snapshot.docs.map((doc) => doc.data()
      )));
     }

   },[roomId])

    const handleSubmit = (e) => {
      e.preventDefault();

      setInput('');
    };

    return (
    <div className="chat"> 

        <div className="chat__header">
         <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`}/>
         <div className="chat__headerInfo">
    <h3>{roomName}</h3>
          <p>Last Seen at ..</p>
        </div>
        <div className="chat__headerRight">
         <IconButton>
         <SearchOutlinedIcon />
         </IconButton>
         <IconButton>
         <AttachFileIcon />
         </IconButton>
         <IconButton>
         <MoreVertIcon />
         </IconButton>

         </div>

        </div>

       <div className="chat__body">
         <p className={`chat__message ${true && "chat__reciever"}`}> <span className="chat__name"> Apurva </span>  HI ALL <span className="chat__timestamp"> 12:00 am</span></p>

       </div>

        <div className="chat__footer">
         <InsertEmoticonIcon />
         <form onSubmit={handleSubmit}> 
             <input value={input} onChange={(e) => setInput(e.target.value)} placeholder= "Type a message" type="text" />
             <button  type="submit" value="Submit" >Send a Message</button>
         </form>
         <MicIcon />

        </div>

    </div> 
    );
}

export default Chat;
